import React from 'react';
import images from '../images/statue1.png'; 
import { useHistory } from 'react-router-dom'; // Import useHistory from react-router-dom



function Page1() {
    const history = useHistory(); 
    const handleNextClick = () => {
      history.push('/page2'); 
    };
  return (
    <div className="bg-blue-100 p-4">
      <h1 className="text-2xl font-semibold text-blue-900">Toad God 01</h1>
      <p className="text-gray-700 mt-2">Multiple Perspectives</p>

    <img src={images} alt="My Image" className="w-full h-auto" />
      <button className="bg-blue-200 hover:bg-blue-600 text-white font-bold py-2 px-4 mt-4 rounded" onClick={handleNextClick} >
        Next

      </button>
    </div>
  );
}



export default Page1;
