    return (
      <div className="bg-green-100 p-4">
        <h1 className="text-2xl font-semibold text-green-900">Page 2</h1>
        <p className="text-gray-700 mt-2">This is Page 2</p>

        {/* Use Link to navigate to Page3 */}
        <Link to="/page3">
          <button className="bg-green-200 hover:bg-green-600 text-white font-bold py-2 px-4 mt-4 rounded">
            Next to Page 3
          </button>
        </Link>
      </div>
    );
  }

