import React, { useState, useEffect } from 'react';
import images from '../images/frame1.png';
import { Link } from 'react-router-dom';
import './Page.css'; // Import your CSS for styling

function Page1() {
  const [showBulletPoints, setShowBulletPoints] = useState(false);
  const [currentLineIndex, setCurrentLineIndex] = useState(0);

  // Define your magical description as an array of bullet points
  const magicalDescription = [
    'Zodiac: Toad',
    'Characteristics: Kind, Warm, Intuitive',
    'Qualities:',
    ' - You are a person that radiates warmth and empathy, drawing people to you effortlessly.',
    ' - Your intuitive nature allows you to navigate life\'s challenges with grace and wisdom.',
    ' - Creativity flows through you, and you often find solace in artistic expression.',
    ' - Like the Toad\'s adaptability, you embrace change and growth with open arms.',
    ' - You possess a deep connection to nature and find inspiration in its beauty.',
    ' - Just as the Toad embodies transformation, you too experience personal growth and evolution.',
    ' - Guided by the stars, you embark on unique and magical life adventures.',
    ' - You are a compassionate listener, offering comfort and understanding to those around you.',
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      if (currentLineIndex < magicalDescription.length) {
        setShowBulletPoints([...showBulletPoints, magicalDescription[currentLineIndex]]);
        setCurrentLineIndex(currentLineIndex + 1);
      } else {
        clearInterval(timer);
      }
    }, 1000); // Adjust the delay as needed

    return () => clearInterval(timer);
  }, [currentLineIndex, magicalDescription, showBulletPoints]);

  return (
    <div className="bg-white-100 p-4">
      <h1 className="text-2xl font-semibold text-blue-900">Toad Zodiac</h1>
      <p className="text-gray-700 mt-2">Multiple Perspectives</p>
      <img
        src={images}
        alt="Toad God 01"
        className="w-full h-auto animate-slide-from-left 3s ease-out"
      />

      {/* Display the magical description with typewriter effect */}
      <div className="magical-description">
        {showBulletPoints.map((line, index) => (
          <div key={index}>{line}</div>
        ))}
      </div>

      <Link to="/page2">
        <button className="bg-blue-200 hover:bg-blue-600 text-white font-bold py-2 px-4 mt-4 rounded">
          Next
        </button>
      </Link>
    </div>
  );
}

export default Page1;
