import React, { useState, useEffect } from 'react';
import images from '../images/frame5.png';
import { Link } from 'react-router-dom';

function Page5() {
  const [reveal, setReveal] = useState(false);

  useEffect(() => {
    // Delay the reveal by 3 seconds
    const timer = setTimeout(() => {
      setReveal(true);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="bg-white-100 p-4">
      <h1 className="text-2xl font-semibold text-blue-900">Toad Zodiac</h1>
      <p className="text-gray-700 mt-2">Multiple Perspectives</p>
      <div className={`image-container ${reveal ? 'animate-reveal' : ''}`}>
        <img src={images} alt="Toad God" />
      </div>
      <Link to="/page6">
        <button className="bg-blue-200 hover:bg-blue-600 text-white font-bold py-2 px-4 mt-4 rounded">
          Next
        </button>
      </Link>
    </div>
  );
}

export default Page5;
